export default function select(){document.querySelectorAll(".custom-select").forEach(t=>{let c=t.querySelector(".custom-select__selected");var e=t.querySelector(".custom-select__options");c.addEventListener("click",()=>{t.classList.toggle("custom-select--open")}),e.addEventListener("click",e=>{e.target.classList.contains("custom-select__option")&&(c.textContent=e.target.textContent,t.classList.remove("custom-select--open"))}),document.addEventListener("click",e=>{t.contains(e.target)||t.classList.remove("custom-select--open")})})}